import React from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { toggleWishlistItem } from '../../redux/actions/wishlistActions';
import { addToCart } from '../../redux/actions/cartActions';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const dispatch = useDispatch();
  const wishlist = useSelector(state => state.wishlist);
  const auth = useSelector(state => state.auth);

  // Check if product is in wishlist
  const isInWishlist = wishlist.items.some(item => item._id === product._id);

  // Handle wishlist toggle
  const handleWishlistToggle = (e) => {
    e.preventDefault();
    if (auth.isAuthenticated) {
      dispatch(toggleWishlistItem(product));
    } else {
      // Redirect to login or show notification
      console.log('Please login to add items to wishlist');
    }
  };

  // Handle add to cart
  const handleAddToCart = (e) => {
    e.preventDefault();
    if (product.countInStock > 0) {
      dispatch(addToCart(product._id, 1));
    }
  };

  // Truncate product name if too long
  const truncateName = (name) => {
    if (name.length > 28) {
      return name.substring(0, 25) + '...';
    }
    return name;
  };

  return (
    <div className="product-card">
      <div className="vinyl-sleeve">
        <div className="record-hole"></div>
        <div className="record-grooves"></div>
        
        <Link to={`/product/${product._id}`} className="product-link">
          <div className="product-image">
            <img 
              src={product.images && product.images.length > 0 ? product.images[0] : '/images/placeholder.jpg'} 
              alt={product.name} 
            />
            {product.category && (
              <div className="product-category-tag">{product.category}</div>
            )}
          </div>
          
          <div className="product-info">
            <h3 className="product-title">{truncateName(product.name)}</h3>
            
            <div className="vinyl-details">
              <div className="vinyl-badges">
                {product.genre && (
                  <span className="vinyl-badge genre">{product.genre}</span>
                )}
                {product.decade && (
                  <span className="vinyl-badge decade">{product.decade}</span>
                )}
                {product.condition && (
                  <span className="vinyl-badge condition">{product.condition}</span>
                )}
              </div>
            </div>
            
            <div className="product-footer">
              <div className="product-price">${product.price.toFixed(2)}</div>
              <button 
                className={`add-to-cart-btn ${product.countInStock === 0 ? 'sold-out' : ''}`}
                onClick={handleAddToCart}
                disabled={product.countInStock === 0}
              >
                <span className="material-icons cart-icon">
                  {product.countInStock === 0 ? 'remove_shopping_cart' : 'add_shopping_cart'}
                </span>
                {product.countInStock === 0 ? 'Sold Out' : 'Add'}
              </button>
            </div>
          </div>
        </Link>
        
        <button 
          className={`wishlist-btn ${isInWishlist ? 'active' : ''}`} 
          onClick={handleWishlistToggle}
        >
          <span className="material-icons">
            {isInWishlist ? 'favorite' : 'favorite_border'}
          </span>
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
