// Application configuration settings

// Store the config in a way that can be modified
const config = {
  // Set to true to disable API calls and use mock data
  useOnlyMockData: true,
  
  // API endpoints
  apiEndpoints: {
    products: '/api/products',
    externalProducts: 'https://api.escuelajs.co/api/v1/products'
  }
};

// Function to toggle mock data usage
export const toggleMockDataMode = () => {
  config.useOnlyMockData = !config.useOnlyMockData;
  console.log(`Mock data mode ${config.useOnlyMockData ? 'enabled' : 'disabled'}`);
  return config.useOnlyMockData;
};

// Export the config object
export default config;
