import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';

const Chat = ({ user, logout }) => {
  const [socket, setSocket] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [roomId, setRoomId] = useState('general');
  const [roomUsers, setRoomUsers] = useState([]);
  const [connected, setConnected] = useState(false);
  const messagesEndRef = useRef(null);

  // Initialize Socket.IO connection
  useEffect(() => {
    const newSocket = io('/socket.io/chat', {
      path: '/socket.io',
      auth: { userId: user.email }
    });

    newSocket.on('connect', () => {
      console.log('Connected to chat server');
      setConnected(true);
      
      // Join the default room
      newSocket.emit('join', {
        roomId: 'general',
        userId: user.email,
        username: user.name
      });
    });

    newSocket.on('disconnect', () => {
      console.log('Disconnected from chat server');
      setConnected(false);
    });

    newSocket.on('message', (msg) => {
      setMessages(prevMessages => [...prevMessages, msg]);
    });

    newSocket.on('userJoined', (data) => {
      setMessages(prevMessages => [
        ...prevMessages, 
        { system: true, message: data.message, timestamp: new Date().toISOString() }
      ]);
    });

    newSocket.on('userLeft', (data) => {
      setMessages(prevMessages => [
        ...prevMessages, 
        { system: true, message: data.message, timestamp: new Date().toISOString() }
      ]);
    });

    newSocket.on('roomUsers', (data) => {
      setRoomUsers(data.users);
    });

    setSocket(newSocket);

    return () => {
      if (newSocket) {
        newSocket.disconnect();
      }
    };
  }, [user.email, user.name]);

  // Auto-scroll to bottom when messages update
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    
    if (message.trim() && socket && connected) {
      socket.emit('sendMessage', {
        roomId,
        message,
        userId: user.email,
        username: user.name
      });
      
      setMessage('');
    }
  };

  const handleJoinRoom = (newRoomId) => {
    if (socket && connected && newRoomId.trim()) {
      // Leave current room
      socket.emit('leaveRoom', { roomId });
      
      // Join new room
      socket.emit('join', {
        roomId: newRoomId,
        userId: user.email,
        username: user.name
      });
      
      setRoomId(newRoomId);
      setMessages([]);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>ConnecShop Chat</h1>
        <button onClick={logout} className="logout-button">Logout</button>
      </div>
      
      <div className="chat-main">
        <div className="room-info">
          <h2>Room: {roomId}</h2>
          <div className="room-controls">
            <input
              type="text"
              placeholder="Enter room name"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
            <button onClick={() => handleJoinRoom(roomId)}>Join Room</button>
          </div>
          
          <div className="online-users">
            <h3>Users in room ({roomUsers.length})</h3>
            <ul>
              {roomUsers.map(u => (
                <li key={u.userId}>{u.username}</li>
              ))}
            </ul>
          </div>
        </div>
        
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div 
              key={index} 
              className={`message ${msg.system ? 'system' : msg.userId === user.email ? 'own' : 'other'}`}
            >
              {!msg.system && (
                <div className="message-user">{msg.userId === user.email ? 'You' : msg.username}</div>
              )}
              <div className="message-content">{msg.message}</div>
              <div className="message-time">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>
      
      <form className="message-form" onSubmit={handleSendMessage}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type a message..."
          disabled={!connected}
        />
        <button type="submit" disabled={!connected}>Send</button>
      </form>
      
      <div className="connection-status">
        Status: {connected ? 'Connected' : 'Disconnected'}
      </div>
    </div>
  );
};

export default Chat;
