import React, { useState } from 'react';
import './ProductCard.css';
import { Link } from 'react-router-dom';
import { useCart } from '../../contexts/CartContext';
import { useAuth } from '../../contexts/AuthContext';
import { useWishlist } from '../../contexts/WishlistContext';
import { toast } from 'react-toastify';

const ProductCard = ({ product }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const { addToCart } = useCart();
  const { currentUser } = useAuth();
  const { addToWishlist, removeFromWishlist, wishlist } = useWishlist();
  
  const {
    _id,
    name,
    price,
    imageUrl,
    category,
    countInStock,
    genre,
    decade,
    condition,
  } = product;
  
  const truncatedName = name.length > 28 ? `${name.substring(0, 25)}...` : name;
  const isInWishlist = wishlist.some(item => item._id === _id);
  
  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (!currentUser) {
      toast.error('Please log in to add items to your cart');
      return;
    }
    
    if (countInStock > 0) {
      addToCart(product);
      toast.success(`Added to cart`);
    } else {
      toast.error('Out of stock');
    }
  };
  
  const handleWishlistToggle = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (!currentUser) {
      toast.error('Please log in to manage your wishlist');
      return;
    }

    if (isInWishlist) {
      removeFromWishlist(_id);
      toast.info(`Removed from wishlist`);
    } else {
      addToWishlist(product);
      toast.success(`Added to wishlist`);
    }
  };
  
  return (
    <div className="product-card">
      <div className="vinyl-sleeve">
        <div className="record-hole"></div>
        <div className="record-grooves"></div>
        
        <Link to={`/product/${_id}`} className="product-link">
          <div className="product-image">
            <img 
              src={imageUrl} 
              alt={name} 
              loading="lazy" 
              onLoad={() => setImageLoaded(true)}
              style={{opacity: imageLoaded ? 1 : 0, transition: 'opacity 0.3s'}}
            />
            <div className="product-category-tag">{category}</div>
          </div>
          
          <div className="product-info">
            <h3 className="product-title">{truncatedName}</h3>
            
            <div className="vinyl-details">
              <div className="vinyl-badges">
                {genre && <span className="vinyl-badge genre">{genre}</span>}
                {condition && <span className="vinyl-badge condition">{condition}</span>}
                {decade && <span className="vinyl-badge decade">{decade}</span>}
              </div>
            </div>
            
            <div className="product-footer">
              <div className="product-price">${price.toFixed(2)}</div>
              <button 
                className={`add-to-cart-btn ${countInStock === 0 ? 'sold-out' : ''}`}
                onClick={handleAddToCart}
                disabled={countInStock === 0}
              >
                <span className="material-icons cart-icon">shopping_cart</span>
                {countInStock > 0 ? 'Add' : 'Sold Out'}
              </button>
            </div>
          </div>
        </Link>
        
        <button 
          className={`wishlist-btn ${isInWishlist ? 'active' : ''}`}
          onClick={handleWishlistToggle}
          title={isInWishlist ? "Remove from Wishlist" : "Add to Wishlist"}
          aria-label={isInWishlist ? "Remove from Wishlist" : "Add to Wishlist"}
        >
          <span className="material-icons">
            {isInWishlist ? 'favorite' : 'favorite_border'}
          </span>
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
