import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import './ProductDetail.css';

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const { addItem } = useCart();

  useEffect(() => {
    const fetchProduct = async () => {
      setLoading(true);
      try {
        // Try to fetch from internal API first
        let response;
        try {
          response = await fetch(`/api/products/${id}`);
          if (!response.ok) throw new Error('Internal API unavailable');
        } catch (internalError) {
          // Fallback to external API if internal fails
          response = await fetch(`https://api.escuelajs.co/api/v1/products/${id}`);
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
        }
        
        const data = await response.json();
        
        // Enhance product with vinyl-specific attributes if needed
        if (!data.genre) {
          const genres = ['Rock', 'Jazz', 'Hip-Hop', 'Electronic', 'Classical', 'Pop', 'Soul', 'Country'];
          data.genre = genres[Math.floor(Math.random() * genres.length)];
        }
        if (!data.decade) {
          const decades = ['60s', '70s', '80s', '90s', '2000s'];
          data.decade = decades[Math.floor(Math.random() * decades.length)];
        }
        if (!data.condition) {
          const conditions = ['Mint', 'Near Mint', 'Very Good Plus', 'Very Good', 'Good', 'Fair'];
          data.condition = conditions[Math.floor(Math.random() * conditions.length)];
        }
        
        setProduct(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0) setQuantity(value);
  };

  const handleAddToCart = () => {
    if (product) {
      addItem({
        ...product,
        quantity
      });
      alert(`Added ${quantity} ${quantity === 1 ? 'copy' : 'copies'} of "${product.title}" to your cart`);
    }
  };

  if (loading) {
    return (
      <div className="product-detail-loading">
        <div className="loader"></div>
        <p>Loading product details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="product-detail-error">
        <h2>Error loading product</h2>
        <p>{error}</p>
        <Link to="/" className="back-btn">Back to Home</Link>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="product-detail-error">
        <h2>Product not found</h2>
        <p>The requested product could not be found.</p>
        <Link to="/" className="back-btn">Back to Home</Link>
      </div>
    );
  }

  return (
    <div className="product-detail-page">
      <div className="product-detail-container">
        <div className="product-images">
          <div className="main-image-container">
            <div className="record-hole-large"></div>
            <img 
              src={product.images?.[0] || 'https://via.placeholder.com/500'} 
              alt={product.title} 
              className="main-image"
            />
          </div>
          <div className="thumbnail-container">
            {product.images?.slice(0, 4).map((image, index) => (
              <img 
                key={index} 
                src={image} 
                alt={`${product.title} - view ${index + 1}`} 
                className="thumbnail-image"
              />
            ))}
          </div>
        </div>
        
        <div className="product-info">
          <div className="product-header">
            <h1 className="product-title">{product.title}</h1>
            <p className="product-price">${parseFloat(product.price).toFixed(2)}</p>
          </div>
          
          <div className="vinyl-info">
            <div className="vinyl-info-item">
              <span className="info-label">Genre:</span>
              <span className="info-value">{product.genre}</span>
            </div>
            <div className="vinyl-info-item">
              <span className="info-label">Decade:</span>
              <span className="info-value">{product.decade}</span>
            </div>
            <div className="vinyl-info-item">
              <span className="info-label">Condition:</span>
              <span className="info-value">{product.condition}</span>
            </div>
          </div>
          
          <div className="product-description">
            <h3>Description</h3>
            <p>{product.description}</p>
          </div>
          
          <div className="purchase-options">
            <div className="quantity-selector">
              <label htmlFor="quantity">Quantity:</label>
              <div className="quantity-controls">
                <button 
                  onClick={() => quantity > 1 && setQuantity(quantity - 1)}
                  className="quantity-btn"
                >
                  -
                </button>
                <input 
                  type="number" 
                  id="quantity" 
                  value={quantity} 
                  onChange={handleQuantityChange}
                  min="1"
                />
                <button 
                  onClick={() => setQuantity(quantity + 1)}
                  className="quantity-btn"
                >
                  +
                </button>
              </div>
            </div>
            
            <button className="add-to-cart-btn" onClick={handleAddToCart}>
              Add to Cart
            </button>
          </div>
          
          <div className="product-meta">
            {product.category && (
              <div className="category-tag">
                <span className="meta-label">Category:</span>
                <span className="meta-value">{product.category.name}</span>
              </div>
            )}
          </div>
        </div>
      </div>
      
      <div className="related-products">
        <h2>You might also like</h2>
        <p>Related products will be displayed here</p>
      </div>
    </div>
  );
};

export default ProductDetail;
