import React from 'react';
import './ProductCard.css';
import { Link } from 'react-router-dom';
import { useCart } from '../../contexts/CartContext';
import { useAuth } from '../../contexts/AuthContext';
import { useWishlist } from '../../contexts/WishlistContext';
import { toast } from 'react-toastify';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { currentUser } = useAuth();
  const { addToWishlist, removeFromWishlist, isInWishlist } = useWishlist();
  
  const {
    _id,
    name,
    price,
    description,
    imageUrl,
    category,
    countInStock,
    genre,
    decade,
    condition,
  } = product;
  
  const truncatedName = name.length > 40 ? `${name.substring(0, 40)}...` : name;
  
  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (!currentUser) {
      toast.error('Please login to add items to your cart');
      return;
    }
    
    if (countInStock < 1) {
      toast.error('Product is out of stock');
      return;
    }
    
    addToCart(product);
    toast.success(`${name} added to cart`);
  };
  
  const handleWishlistToggle = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (!currentUser) {
      toast.error('Please login to use the wishlist');
      return;
    }
    
    const isProductInWishlist = isInWishlist(_id);
    
    if (isProductInWishlist) {
      removeFromWishlist(_id);
      toast.info(`${name} removed from wishlist`);
    } else {
      addToWishlist(product);
      toast.success(`${name} added to wishlist`);
    }
  };
  
  return (
    <div className="product-card vinyl-sleeve">
      <div className="record-hole"></div>
      <div className="record-grooves"></div>
      <Link to={`/product/${_id}`} className="product-link">
        <div className="product-image">
          <img src={imageUrl} alt={name} loading="lazy" />
          {category && <div className="product-category-tag">{category}</div>}
          <div className="quick-actions">
            <button
              className="quick-action-btn"
              onClick={handleWishlistToggle}
              aria-label={isInWishlist(_id) ? "Remove from wishlist" : "Add to wishlist"}
            >
              <span className="material-icons">
                {isInWishlist(_id) ? "favorite" : "favorite_border"}
              </span>
            </button>
          </div>
        </div>
        
        <div className="product-info">
          <h3 className="product-title">{truncatedName}</h3>
          
          <div className="vinyl-details">
            <div className="vinyl-badges">
              {genre && <span className="vinyl-badge genre">{genre}</span>}
              {decade && <span className="vinyl-badge decade">{decade}</span>}
              {condition && <span className="vinyl-badge condition">{condition}</span>}
            </div>
          </div>
          
          <div className="product-footer">
            <div className="product-price">${price.toFixed(2)}</div>
            <button 
              className="add-to-cart-btn" 
              onClick={handleAddToCart}
              disabled={countInStock < 1}
            >
              <span className="material-icons cart-icon">shopping_cart</span>
              <span>{countInStock < 1 ? "Sold Out" : "Add"}</span>
            </button>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
