import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product, onAddToCart }) => {
  const { 
    id, 
    title, 
    price, 
    images, 
    description, 
    // Transformed product fields
    genre,
    decade,
    condition,
    artist,
    productType
  } = product;

  const [isHovered, setIsHovered] = useState(false);

  // Use the rating from the product or generate one
  const rating = product.rating || (3.5 + Math.random() * 1.5).toFixed(1);

  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onAddToCart(product);
  };

  const handleQuickView = (e) => {
    e.preventDefault();
    e.stopPropagation();
    // In a real app, this would open a quick view modal
    alert(`Quick View: ${title}`);
  };

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;

    for (let i = 1; i <= 5; i++) {
      if (i <= fullStars) {
        stars.push(<span key={i} className="material-icons star-icon">star</span>);
      } else if (i === fullStars + 1 && hasHalfStar) {
        stars.push(<span key={i} className="material-icons star-icon">star_half</span>);
      } else {
        stars.push(<span key={i} className="material-icons star-icon">star_outline</span>);
      }
    }

    return stars;
  };

  // Get condition color based on the condition
  const getConditionColor = (condition) => {
    switch(condition) {
      case 'Mint': return '#4ade80'; // Green
      case 'Near Mint': return '#22d3ee'; // Teal
      case 'Very Good Plus': return '#60a5fa'; // Blue
      case 'Very Good': return '#818cf8'; // Indigo
      case 'Good': return '#e879f9'; // Purple
      case 'Fair': return '#fb7185'; // Red
      default: return '#f9a8d4'; // Pink
    }
  };

  // Render appropriate icon based on product type
  const renderProductTypeIcon = () => {
    switch(productType) {
      case 'vinyl':
        return <span className="material-icons product-type-icon">album</span>;
      case 'turntable':
        return <span className="material-icons product-type-icon">music_note</span>;
      case 'accessory':
        return <span className="material-icons product-type-icon">headphones</span>;
      default:
        return null;
    }
  };

  // Render vinyl-specific info
  const renderVinylInfo = () => (
    <div className="vinyl-details">
      <div className="vinyl-artist">
        <span className="artist-label">Artist:</span>
        <span className="artist-name">{artist}</span>
      </div>
      <div className="vinyl-detail">
        <span className="detail-label">Genre:</span>
        <span className="detail-value">{genre}</span>
      </div>
      <div className="vinyl-detail">
        <span className="detail-label">Decade:</span>
        <span className="detail-value">{decade}</span>
      </div>
      <div className="vinyl-detail">
        <span className="detail-label">Condition:</span>
        <span 
          className="detail-value condition-tag"
          style={{ 
            backgroundColor: getConditionColor(condition),
            color: '#000000'
          }}
        >
          {condition}
        </span>
      </div>
    </div>
  );

  // Render turntable-specific info
  const renderTurntableInfo = () => (
    <div className="turntable-details">
      <div className="spec-item">
        <span className="spec-icon material-icons">speed</span>
        <span className="spec-text">33/45/78 RPM</span>
      </div>
      <div className="spec-item">
        <span className="spec-icon material-icons">equalizer</span>
        <span className="spec-text">Built-in Preamp</span>
      </div>
      <div className="spec-item">
        <span className="spec-icon material-icons">settings</span>
        <span className="spec-text">Anti-Skating</span>
      </div>
    </div>
  );

  // Render accessory-specific info
  const renderAccessoryInfo = () => (
    <div className="accessory-details">
      <div className="usage-tag">Essential Collection Item</div>
      <div className="compatibility">
        <span className="material-icons">check_circle</span>
        <span>Compatible with all systems</span>
      </div>
    </div>
  );

  // Get CSS classes based on product type
  const getCardClasses = () => {
    const baseClass = "product-card";
    switch(productType) {
      case 'vinyl':
        return `${baseClass} vinyl-sleeve`;
      case 'turntable':
        return `${baseClass} turntable-card`;
      case 'accessory':
        return `${baseClass} accessory-card`;
      default:
        return baseClass;
    }
  };

  return (
    <div 
      className={getCardClasses()}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <Link to={`/product/${id}`} className="product-link">
        {productType === 'vinyl' && <div className="record-hole"></div>}

        <div className={`product-image ${productType}-image`}>
          <img 
            src={images?.[0] || 'https://via.placeholder.com/300'} 
            alt={title} 
            loading="lazy"
          />

          <div className="product-type-badge">
            {renderProductTypeIcon()}
            <span>{productType}</span>
          </div>

          {genre && productType === 'vinyl' && (
            <span className="genre-badge">{genre}</span>
          )}

          {isHovered && (
            <div className="quick-actions">
              <button 
                className="quick-action-btn"
                onClick={handleQuickView}
                aria-label="Quick view"
              >
                <span className="material-icons">visibility</span>
              </button>
              <button 
                className="quick-action-btn"
                onClick={handleAddToCart}
                aria-label="Add to cart"
              >
                <span className="material-icons">add_shopping_cart</span>
              </button>
            </div>
          )}
        </div>

        <div className={`product-info ${productType}-info`}>
          {/* Title section with conditional formatting */}
          {productType === 'vinyl' ? (
            <div className="vinyl-title-section">
              <h3 className="product-title">{title}</h3>
              <p className="vinyl-artist-subtitle">by {artist}</p>
            </div>
          ) : (
            <h3 className="product-title">{title}</h3>
          )}

          {/* Product-type specific details */}
          {productType === 'vinyl' && renderVinylInfo()}
          {productType === 'turntable' && renderTurntableInfo()}
          {productType === 'accessory' && renderAccessoryInfo()}

          <div className="product-rating">
            {renderStars(rating)}
            <span className="rating-number">{rating}</span>
          </div>

          <p className="product-description">
            {description && description.length > 60
              ? `${description.substring(0, 60)}...` 
              : description}
          </p>

          <div className="product-footer">
            <span className={`product-price ${productType}-price`}>
              ${parseFloat(price).toFixed(2)}
            </span>
            <button 
              className="add-to-cart-btn"
              onClick={handleAddToCart}
              aria-label={`Add ${title} to cart`}
            >
              <span className="material-icons cart-icon">shopping_cart</span>
              Add to Cart
            </button>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
