import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/Product/ProductCard';
import { useCart } from '../contexts/CartContext';
import { useProducts } from '../contexts/ProductContext';
import './Home.css';

const Home = () => {
  // Get products and methods from ProductContext
  const { 
    products, 
    loading, 
    error, 
    fetchProducts,
    loadMore 
  } = useProducts();

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [productsByType, setProductsByType] = useState({
    vinyl: [],
    turntable: [],
    accessory: []
  });

  const { addItem } = useCart();

  // Initialize categories based on product types
  const categories = [
    { id: 'vinyl', name: 'Vinyl Records' },
    { id: 'turntable', name: 'Turntables' },
    { id: 'accessory', name: 'Accessories' }
  ];

  // Fetch products on mount
  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // Organize products by type when products change
  useEffect(() => {
    if (products.length > 0) {
      const vinylProducts = products.filter(p => p.productType === 'vinyl');
      const turntableProducts = products.filter(p => p.productType === 'turntable');
      const accessoryProducts = products.filter(p => p.productType === 'accessory');

      setProductsByType({
        vinyl: vinylProducts,
        turntable: turntableProducts,
        accessory: accessoryProducts
      });
    }
  }, [products]);

  // Handle add to cart
  const handleAddToCart = (product) => {
    addItem(product);
    showNotification(`${product.title} added to cart!`);
  };

  // Simple notification system
  const showNotification = (message) => {
    const notification = document.createElement('div');
    notification.className = 'notification';
    notification.innerHTML = `
      <span class="material-icons">check_circle</span>
      <p>${message}</p>
    `;
    document.body.appendChild(notification);

    // Fade in
    setTimeout(() => {
      notification.classList.add('show');
    }, 10);

    // Remove after 3 seconds
    setTimeout(() => {
      notification.classList.remove('show');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 3000);
  };

  // Filter products by category and search query
  const getFilteredProducts = () => {
    return products.filter(product => {
      const matchesCategory = selectedCategory === 'all' || product.productType === selectedCategory;
      const matchesSearch = product.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            product.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                            product.genre?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                            product.artist?.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesCategory && matchesSearch;
    });
  };

  // Get featured vinyl records (4 records)
  const getFeaturedVinyls = () => {
    return productsByType.vinyl.slice(0, 4);
  };

  // Get featured turntables (2 turntables)
  const getFeaturedTurntables = () => {
    return productsByType.turntable.slice(0, 2);
  };

  // Get featured accessories (3 accessories)
  const getFeaturedAccessories = () => {
    return productsByType.accessory.slice(0, 3);
  };

  // Get trendy new releases (random 4 vinyl records with higher prices)
  const getNewReleases = () => {
    return productsByType.vinyl
      .filter(product => parseFloat(product.price) > 100)
      .sort(() => 0.5 - Math.random())
      .slice(0, 4);
  };

  return (
    <div className="home-container">
      {/* Hero Section with Vinyl Background */}
      <div className="hero-section">
        <div className="hero-overlay"></div>
        <div className="hero-content">
          <h1>Welcome to Vinyl Vault</h1>
          <p className="tagline">Rediscover Analog Excellence</p>
          <button className="hero-button" onClick={() => document.getElementById('featured-categories').scrollIntoView({behavior: 'smooth'})}>
            <span className="material-icons">album</span>
            Explore Collection
          </button>
        </div>
      </div>

      {/* Featured Categories Section */}
      <div id="featured-categories" className="categories-section">
        <h2>Featured Categories</h2>
        <div className="categories-grid">
          <div className="category-card vinyl-category">
            <div className="category-image">
              <img src="https://images.unsplash.com/photo-1544427920-c49ccfb85579?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80" alt="Vinyl Records" />
            </div>
            <div className="category-info">
              <h3>Vinyl Records</h3>
              <p>Explore our collection of classic and new vinyl records</p>
              <Link to="/products/vinyl" className="category-button">Browse Records</Link>
            </div>
          </div>

          <div className="category-card turntable-category">
            <div className="category-image">
              <img src="https://images.unsplash.com/photo-1545454675-3531b543be5d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80" alt="Turntables" />
            </div>
            <div className="category-info">
              <h3>Turntables</h3>
              <p>Premium turntables for the perfect listening experience</p>
              <Link to="/products/turntable" className="category-button">Shop Turntables</Link>
            </div>
          </div>

          <div className="category-card accessory-category">
            <div className="category-image">
              <img src="https://www.billboard.com/wp-content/uploads/2023/08/record-player-stand.jpg?w=1024" alt="Accessories" className="accessories-image" />
            </div>
            <div className="category-info">
              <h3>Accessories</h3>
              <p>Essential accessories for vinyl enthusiasts</p>
              <Link to="/products/accessory" className="category-button">View Accessories</Link>
            </div>
          </div>
        </div>
      </div>

      {/* Trade-In Banner */}
      <div className="trade-in-banner">
        <div className="trade-in-content">
          <h2>Trade-In Program</h2>
          <p>Upgrade your collection by trading in your old vinyl records and equipment</p>
          <Link to="/trade-in" className="trade-in-button">Learn More</Link>
        </div>
      </div>

      {/* Featured Vinyl Records Section */}
      {!loading && !error && productsByType.vinyl.length > 0 && (
        <div className="featured-section featured-vinyl-section">
          <div className="section-header">
            <h2>Featured Vinyl Records</h2>
            <Link to="/products/vinyl" className="view-all-link">
              View All <span className="material-icons">arrow_forward</span>
            </Link>
          </div>
          <div className="featured-grid">
            {getFeaturedVinyls().map(product => (
              <ProductCard 
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
          </div>
        </div>
      )}

      {/* Featured Turntables Section */}
      {!loading && !error && productsByType.turntable.length > 0 && (
        <div className="featured-section featured-turntable-section">
          <div className="section-header">
            <h2>Premium Turntables</h2>
            <Link to="/products/turntable" className="view-all-link">
              View All <span className="material-icons">arrow_forward</span>
            </Link>
          </div>
          <div className="featured-grid">
            {getFeaturedTurntables().map(product => (
              <ProductCard 
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
          </div>
        </div>
      )}

      {/* New Releases Section */}
      {!loading && !error && productsByType.vinyl.length > 0 && (
        <div className="featured-section new-releases-section">
          <div className="section-header">
            <h2>New Releases</h2>
            <Link to="/new-releases" className="view-all-link">
              View All <span className="material-icons">arrow_forward</span>
            </Link>
          </div>
          <div className="featured-grid">
            {getNewReleases().map(product => (
              <ProductCard 
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
          </div>
        </div>
      )}

      {/* Featured Accessories Section */}
      {!loading && !error && productsByType.accessory.length > 0 && (
        <div className="featured-section featured-accessory-section">
          <div className="section-header">
            <h2>Essential Accessories</h2>
            <Link to="/products/accessory" className="view-all-link">
              View All <span className="material-icons">arrow_forward</span>
            </Link>
          </div>
          <div className="featured-grid">
            {getFeaturedAccessories().map(product => (
              <ProductCard 
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
          </div>
        </div>
      )}

      {/* All Products Section */}
      <div id="products-section" className="main-products-section">
        <div className="section-header">
          <h2>Browse All Products</h2>
          <div className="filter-controls">
            <div className="search-bar">
              <span className="material-icons search-icon">search</span>
              <input 
                type="text" 
                placeholder="Search products..." 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>

            <div className="category-filter">
              <span>Category:</span>
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="all">All Categories</option>
                {categories.map(category => (
                  <option key={category.id} value={category.id}>{category.name}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {loading ? (
          <div className="loading-container">
            <div className="loader"></div>
            <p>Loading products...</p>
          </div>
        ) : error ? (
          <div className="error-container">
            <span className="material-icons error-icon">error</span>
            <p>Error: {error}</p>
            <button onClick={() => fetchProducts()}>
              <span className="material-icons">refresh</span> Retry
            </button>
          </div>
        ) : (
          <>
            {getFilteredProducts().length > 0 ? (
              <>
                <div className="products-grid">
                  {getFilteredProducts().map(product => (
                    <ProductCard 
                      key={product.id}
                      product={product}
                      onAddToCart={handleAddToCart}
                    />
                  ))}
                </div>
                <div className="load-more-container">
                  <button 
                    className="load-more-button" 
                    onClick={loadMore}
                    disabled={loading}
                  >
                    <span className="material-icons">expand_more</span>
                    Load More Products
                  </button>
                </div>
              </>
            ) : (
              <div className="no-products">
                <span className="material-icons">search_off</span>
                <p>No products found. Try a different search or category.</p>
                <button onClick={() => {setSearchQuery(''); setSelectedCategory('all');}}>
                  <span className="material-icons">refresh</span> Reset Filters
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Home;
