import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/Product/ProductCard';
import { useCart } from '../contexts/CartContext';
import { useProducts } from '../contexts/ProductContext';
import './Home.css';

const Home = () => {
  // State for carousel
  const [activeSlide, setActiveSlide] = useState(0);
  const carouselRef = useRef(null);

  // Get products and cart functions from contexts
  const { 
    products, 
    loading, 
    error, 
    fetchProducts 
  } = useProducts();
  const { addItem } = useCart();

  // Handle add to cart
  const handleAddToCart = (product) => {
    addItem(product);
    showNotification(`${product.title} added to cart!`);
  };

  // Simple notification system
  const showNotification = (message) => {
    const notification = document.createElement('div');
    notification.className = 'notification';
    notification.innerHTML = `
      <span class="material-icons">check_circle</span>
      <p>${message}</p>
    `;
    document.body.appendChild(notification);

    // Fade in
    setTimeout(() => {
      notification.classList.add('show');
    }, 10);

    // Remove after 3 seconds
    setTimeout(() => {
      notification.classList.remove('show');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 3000);
  };

  // Fetch products on mount
  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // Filter products by type
  const getProductsByType = (type) => {
    return products.filter(product => product.productType === type);
  };

  // Get top vinyl records (highest rated)
  const vinylRecords = getProductsByType('vinyl')
    .sort((a, b) => (b.rating || 4.0) - (a.rating || 4.0))
    .slice(0, 6);

  // Get featured turntables
  const turntables = getProductsByType('turntable')
    .sort((a, b) => parseFloat(b.price) - parseFloat(a.price))
    .slice(0, 3);

  // Get essential accessories
  const accessories = getProductsByType('accessory')
    .sort((a, b) => parseFloat(a.price) - parseFloat(b.price))
    .slice(0, 4);

  // Carousel control functions
  const nextSlide = () => {
    if (carouselRef.current) {
      const totalSlides = vinylRecords.length;
      setActiveSlide((prev) => (prev + 1) % totalSlides);

      // Scroll to the new active slide
      const slideWidth = carouselRef.current.offsetWidth;
      carouselRef.current.scrollTo({
        left: ((activeSlide + 1) % totalSlides) * slideWidth,
        behavior: 'smooth'
      });
    }
  };

  const prevSlide = () => {
    if (carouselRef.current) {
      const totalSlides = vinylRecords.length;
      setActiveSlide((prev) => (prev - 1 + totalSlides) % totalSlides);

      // Scroll to the new active slide
      const slideWidth = carouselRef.current.offsetWidth;
      carouselRef.current.scrollTo({
        left: ((activeSlide - 1 + totalSlides) % totalSlides) * slideWidth,
        behavior: 'smooth'
      });
    }
  };

  return (
    <div className="home-container">
      {/* Hero Section with Vinyl Background */}
      <div className="hero-section">
        <div className="hero-overlay"></div>
        <div className="hero-content">
          <h1>Welcome to Vinyl Vault</h1>
          <p className="tagline">Rediscover Analog Excellence</p>
          <button className="hero-button" onClick={() => document.getElementById('vinyl-carousel').scrollIntoView({behavior: 'smooth'})}>
            <span className="material-icons">album</span>
            Explore Collection
          </button>
        </div>
      </div>

      {/* Vinyl Records Carousel Section */}
      {!loading && !error && vinylRecords.length > 0 && (
        <section id="vinyl-carousel" className="vinyl-carousel-section">
          <div className="section-header vinyl-header">
            <h2>Top-Rated Vinyl Records</h2>
            <div className="carousel-controls">
              <button 
                className="carousel-control prev-btn" 
                onClick={prevSlide}
                aria-label="Previous slide"
              >
                <span className="material-icons">chevron_left</span>
              </button>
              <button 
                className="carousel-control next-btn" 
                onClick={nextSlide}
                aria-label="Next slide"
              >
                <span className="material-icons">chevron_right</span>
              </button>
            </div>
          </div>

          <div className="carousel-container">
            <div className="carousel-track" ref={carouselRef}>
              {vinylRecords.map((record, index) => (
                <div 
                  key={record.id} 
                  className={`carousel-slide ${index === activeSlide ? 'active' : ''}`}
                >
                  <div className="record-spotlight">
                    <div className="record-image-container">
                      <img 
                        src={record.images?.[0] || 'https://via.placeholder.com/500'} 
                        alt={record.title} 
                        className="record-image"
                      />
                      <div className="record-disk"></div>
                    </div>

                    <div className="record-details">
                      <h3 className="record-title">{record.title}</h3>
                      <p className="record-artist">{record.artist}</p>

                      <div className="record-specs">
                        <div className="record-spec">
                          <span className="spec-name">Genre:</span>
                          <span className="spec-value">{record.genre}</span>
                        </div>
                        <div className="record-spec">
                          <span className="spec-name">Era:</span>
                          <span className="spec-value">{record.decade}</span>
                        </div>
                        <div className="record-spec">
                          <span className="spec-name">Condition:</span>
                          <span className="record-condition">{record.condition}</span>
                        </div>
                      </div>

                      <p className="record-description">{record.description}</p>

                      <div className="record-actions">
                        <span className="record-price">${parseFloat(record.price).toFixed(2)}</span>
                        <button 
                          className="add-to-cart-btn vinyl-btn"
                          onClick={() => handleAddToCart(record)}
                        >
                          <span className="material-icons">shopping_cart</span>
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="carousel-indicators">
            {vinylRecords.map((_, index) => (
              <button
                key={index}
                className={`carousel-indicator ${index === activeSlide ? 'active' : ''}`}
                onClick={() => setActiveSlide(index)}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>
        </section>
      )}

      {/* Featured Categories Section */}
      <div id="featured-categories" className="categories-section">
        <h2>Explore Our Categories</h2>
        <div className="categories-grid">
          <div className="category-card">
            <div className="category-image">
              <img src="https://images.unsplash.com/photo-1544427920-c49ccfb85579?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80" alt="Vinyl Records" />
            </div>
            <div className="category-info">
              <h3>Vinyl Records</h3>
              <p>Explore our collection of classic and new vinyl records</p>
              <Link to="/vinyl" className="category-button">Browse Records</Link>
            </div>
          </div>

          <div className="category-card">
            <div className="category-image">
              <img src="https://images.unsplash.com/photo-1545454675-3531b543be5d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80" alt="Turntables" />
            </div>
            <div className="category-info">
              <h3>Turntables</h3>
              <p>Premium turntables for the perfect listening experience</p>
              <Link to="/turntables" className="category-button">Shop Turntables</Link>
            </div>
          </div>

          <div className="category-card">
            <div className="category-image">
              <img src="https://www.billboard.com/wp-content/uploads/2023/08/record-player-stand.jpg?w=1024" alt="Accessories" className="accessories-image" />
            </div>
            <div className="category-info">
              <h3>Accessories</h3>
              <p>Essential accessories for vinyl enthusiasts</p>
              <Link to="/accessories" className="category-button">View Accessories</Link>
            </div>
          </div>
        </div>
      </div>

      {/* Featured Turntables Comparison Section */}
      {!loading && !error && turntables.length > 0 && (
        <section className="turntables-section">
          <div className="section-header turntable-header">
            <h2>Featured Turntables</h2>
            <Link to="/turntables" className="view-all-link">
              View All <span className="material-icons">arrow_forward</span>
            </Link>
          </div>

          <div className="turntables-grid">
            {turntables.map(turntable => (
              <div key={turntable.id} className="turntable-comparison-card">
                <div className="turntable-image-container">
                  <img 
                    src={turntable.images?.[0] || 'https://via.placeholder.com/400'} 
                    alt={turntable.title} 
                    className="turntable-image"
                  />
                  <div className="turntable-overlay">
                    <span className="material-icons turntable-icon">music_note</span>
                  </div>
                </div>

                <div className="turntable-content">
                  <h3 className="turntable-title">{turntable.title}</h3>

                  <div className="turntable-features">
                    <div className="feature">
                      <span className="feature-icon material-icons">speed</span>
                      <span className="feature-text">{turntable.speed || "Multiple Speeds"}</span>
                    </div>
                    <div className="feature">
                      <span className="feature-icon material-icons">graphic_eq</span>
                      <span className="feature-text">{turntable.preamp ? "Built-in Preamp" : "No Preamp"}</span>
                    </div>
                    <div className="feature">
                      <span className="feature-icon material-icons">usb</span>
                      <span className="feature-text">{turntable.connectivity || "USB Connection"}</span>
                    </div>
                  </div>

                  <div className="turntable-price-row">
                    <span className="turntable-price">${parseFloat(turntable.price).toFixed(2)}</span>
                    <button 
                      className="add-to-cart-btn turntable-btn"
                      onClick={() => handleAddToCart(turntable)}
                    >
                      Add to Cart
                    </button>
                  </div>

                  <Link to={`/product/${turntable.id}`} className="compare-link">
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Essential Accessories Section */}
      {!loading && !error && accessories.length > 0 && (
        <section className="accessories-section">
          <div className="section-header accessory-header">
            <h2>Must-Have Accessories</h2>
            <Link to="/accessories" className="view-all-link">
              View All <span className="material-icons">arrow_forward</span>
            </Link>
          </div>

          <div className="must-have-grid">
            {accessories.map(accessory => (
              <div key={accessory.id} className="accessory-card">
                <div className="accessory-image-wrap">
                  <img 
                    src={accessory.images?.[0] || 'https://via.placeholder.com/200'} 
                    alt={accessory.title}
                    className="accessory-image"
                  />
                </div>

                <div className="accessory-info">
                  <h3 className="accessory-title">{accessory.title}</h3>
                  <p className="accessory-description">
                    {accessory.description && accessory.description.substring(0, 80)}...
                  </p>

                  <div className="accessory-footer">
                    <span className="accessory-price">${parseFloat(accessory.price).toFixed(2)}</span>
                    <button 
                      className="add-to-cart-btn accessory-btn"
                      onClick={() => handleAddToCart(accessory)}
                    >
                      <span className="material-icons">add_shopping_cart</span>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Trade-In Banner */}
      <div className="trade-in-banner">
        <div className="trade-in-content">
          <h2>Trade-In Program</h2>
          <p>Upgrade your collection by trading in your old vinyl records and equipment</p>
          <Link to="/trade-in" className="trade-in-button">Learn More</Link>
        </div>
      </div>

      {/* Loading & Error States */}
      {loading && (
        <div className="loading-container">
          <div className="vinyl-loader">
            <div className="vinyl-record">
              <div className="vinyl-label"></div>
            </div>
          </div>
          <p>Loading vinyl collection...</p>
        </div>
      )}

      {error && (
        <div className="error-container">
          <span className="material-icons error-icon">error</span>
          <p>Error: {error}</p>
          <button onClick={() => fetchProducts()}>
            <span className="material-icons">refresh</span> Retry
          </button>
        </div>
      )}
    </div>
  );
};

export default Home;
