import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import ProductCard from '../components/Product/ProductCard';
import { useCart } from '../contexts/CartContext';
import './Search.css';

const Search = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchPerformed, setSearchPerformed] = useState(false);
  
  const { addItem } = useCart();
  const location = useLocation();
  
  // Check for query parameters on mount and when URL changes
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const queryFromUrl = queryParams.get('query');
    
    if (queryFromUrl) {
      setSearchQuery(queryFromUrl);
      performSearch(queryFromUrl);
    }
  }, [location.search]);
  
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      performSearch(searchQuery);
      
      // Update URL with search query for sharing/bookmarking
      const url = new URL(window.location);
      url.searchParams.set('query', searchQuery);
      window.history.pushState({}, '', url);
    }
  };
  
  const performSearch = async (query) => {
    setLoading(true);
    setError(null);
    setSearchPerformed(true);
    
    try {
      // Try to fetch from internal API first
      let response;
      try {
        response = await fetch(`/api/products?query=${encodeURIComponent(query)}`);
        if (!response.ok) throw new Error('Internal API unavailable');
      } catch (internalError) {
        // Fallback to external API if internal fails
        response = await fetch(`https://api.escuelajs.co/api/v1/products?title=${encodeURIComponent(query)}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
      }
      
      const data = await response.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };
  
  const handleAddToCart = (product) => {
    addItem(product);
    alert(`${product.title} added to cart!`);
  };
  
  return (
    <div className="search-page">
      <div className="search-header">
        <h1>Search Products</h1>
        <p>Find exactly what you're looking for</p>
      </div>
      
      <div className="search-container">
        <form onSubmit={handleSubmit} className="search-form">
          <input
            type="text"
            value={searchQuery}
            onChange={handleSearchChange}
            placeholder="Search for products..."
            className="search-input"
          />
          <button 
            type="submit" 
            className="search-button"
            disabled={loading || !searchQuery.trim()}
          >
            {loading ? 'Searching...' : 'Search'}
          </button>
        </form>
        
        {error && (
          <div className="search-error">
            <p>Error: {error}</p>
            <button onClick={() => performSearch(searchQuery)}>Try Again</button>
          </div>
        )}
        
        {loading ? (
          <div className="search-loading">
            <div className="loader"></div>
            <p>Searching for products...</p>
          </div>
        ) : searchPerformed ? (
          <>
            <div className="search-results-info">
              <h2>
                {products.length === 0 
                  ? 'No products found' 
                  : `Found ${products.length} product${products.length === 1 ? '' : 's'}`}
              </h2>
              {searchQuery && <p>Results for: "{searchQuery}"</p>}
            </div>
            
            {products.length > 0 ? (
              <div className="products-grid">
                {products.map(product => (
                  <ProductCard 
                    key={product.id}
                    product={product}
                    onAddToCart={handleAddToCart}
                  />
                ))}
              </div>
            ) : (
              <div className="no-results">
                <p>No products match your search criteria.</p>
                <p>Try a different search term or browse our categories.</p>
              </div>
            )}
          </>
        ) : (
          <div className="search-prompt">
            <p>Enter a search term to find products</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Search;
