import React, { createContext, useContext, useEffect, useState, useCallback, useRef } from 'react';
import io from 'socket.io-client';
import { useAuth } from './AuthContext';

const SocketContext = createContext();

export function useSocket() {
  return useContext(SocketContext);
}

export function SocketProvider({ children }) {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [roomUsers, setRoomUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [reconnectAttempts, setReconnectAttempts] = useState(0);
  const [isReconnecting, setIsReconnecting] = useState(false);
  const [typingUsers, setTypingUsers] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [messageStatus, setMessageStatus] = useState({});
  const { currentUser } = useAuth();
  
  // Keep a ref for timeout to clear typing status
  const typingTimeoutRef = useRef(null);
  const maxReconnectAttempts = 5;
  
  // Create socket connection
  const connectSocket = useCallback(() => {
    if (!currentUser) return null;
    
    const newSocket = io(process.env.REACT_APP_API_URL || 'http://localhost:5000/socket.io/chat', {
      path: '/socket.io',
      reconnection: true,
      reconnectionAttempts: maxReconnectAttempts,
      reconnectionDelay: 1000,
      reconnectionDelayMax: 5000,
      timeout: 10000,
    });
    
    return newSocket;
  }, [currentUser]);
  
  // Handle reconnection logic
  const attemptReconnect = useCallback(() => {
    if (reconnectAttempts >= maxReconnectAttempts) {
      console.error('Max reconnection attempts reached');
      setIsReconnecting(false);
      return;
    }
    
    setIsReconnecting(true);
    const newSocket = connectSocket();
    
    if (newSocket) {
      setSocket(newSocket);
      setReconnectAttempts(prev => prev + 1);
    }
  }, [connectSocket, reconnectAttempts, maxReconnectAttempts]);
  
  // Setup socket connection
  useEffect(() => {
    // Only connect if user is authenticated
    if (currentUser) {
      // Create socket connection
      const newSocket = connectSocket();
      
      if (!newSocket) return;
      
      // Socket event listeners
      newSocket.on('connect', () => {
        console.log('Socket connected');
        setIsConnected(true);
        setIsReconnecting(false);
        setReconnectAttempts(0);
        
        // Rejoin room if was previously in one
        if (currentRoom) {
          newSocket.emit('join', {
            roomId: currentRoom,
            userId: currentUser.id,
            username: currentUser.name || currentUser.email
          });
        }
      });
      
      newSocket.on('disconnect', () => {
        console.log('Socket disconnected');
        setIsConnected(false);
        attemptReconnect();
      });
      
      newSocket.on('connect_error', (error) => {
        console.error('Socket connection error:', error);
        setIsConnected(false);
        attemptReconnect();
      });

      // Event listeners for chat functionality
      newSocket.on('message', (messageData) => {
        console.log('Message received:', messageData);
        
        // Add to notifications if not in current room
        if (messageData.roomId !== currentRoom) {
          setNotifications(prev => [...prev, {
            id: Date.now(),
            message: `New message from ${messageData.username} in room ${messageData.roomId}`,
            roomId: messageData.roomId,
            timestamp: new Date()
          }]);
        }
        
        // Acknowledge message receipt
        newSocket.emit('messageReceived', {
          messageId: messageData.id,
          roomId: messageData.roomId
        });
      });

      newSocket.on('userJoined', (data) => {
        console.log('User joined:', data);
      });

      newSocket.on('userLeft', (data) => {
        console.log('User left:', data);
      });

      newSocket.on('roomUsers', (data) => {
        console.log('Users in room:', data);
        setRoomUsers(data.users);
      });
      
      // Typing indicator events
      newSocket.on('userTyping', (data) => {
        setTypingUsers(prev => {
          if (!prev.some(user => user.id === data.userId)) {
            return [...prev, { id: data.userId, username: data.username }];
          }
          return prev;
        });
      });
      
      newSocket.on('userStoppedTyping', (data) => {
        setTypingUsers(prev => prev.filter(user => user.id !== data.userId));
      });
      
      // Message status updates
      newSocket.on('messageDelivered', (data) => {
        setMessageStatus(prev => ({
          ...prev,
          [data.messageId]: { ...prev[data.messageId], delivered: true, deliveredAt: new Date() }
        }));
      });
      
      newSocket.on('messageRead', (data) => {
        setMessageStatus(prev => ({
          ...prev,
          [data.messageId]: { ...prev[data.messageId], read: true, readAt: new Date() }
        }));
      });
      
      setSocket(newSocket);
      
      // Cleanup function
      return () => {
        if (currentRoom) {
          newSocket.emit('leaveRoom', { roomId: currentRoom });
        }
        
        // Clear any existing timeouts
        if (typingTimeoutRef.current) {
          clearTimeout(typingTimeoutRef.current);
        }
        
        newSocket.close();
      };
    } else {
      // If user logs out, disconnect socket
      if (socket) {
        if (currentRoom) {
          socket.emit('leaveRoom', { roomId: currentRoom });
        }
        socket.close();
        setSocket(null);
        setIsConnected(false);
        setCurrentRoom(null);
        setRoomUsers([]);
        setTypingUsers([]);
        // Clear notifications and message status on logout
        setNotifications([]);
        setMessageStatus({});
      }
    }
  }, [currentUser, connectSocket, attemptReconnect, currentRoom, socket]);
  
  // Functions to emit events
  const sendMessage = (roomId, message) => {
    if (socket && isConnected && currentUser) {
      const messageId = `${Date.now()}-${currentUser.id}`;
      
      socket.emit('sendMessage', {
        id: messageId,
        roomId,
        message,
        userId: currentUser.id,
        username: currentUser.name || currentUser.email
      });
      
      // Initialize message status
      setMessageStatus(prev => ({
        ...prev,
        [messageId]: { sent: true, sentAt: new Date() }
      }));
      
      return messageId;
    }
    return null;
  };
  
  const joinRoom = (roomId) => {
    if (socket && isConnected && currentUser) {
      // Leave current room if any
      if (currentRoom && currentRoom !== roomId) {
        socket.emit('leaveRoom', { roomId: currentRoom });
      }
      
      // Join new room
      socket.emit('join', {
        roomId,
        userId: currentUser.id,
        username: currentUser.name || currentUser.email
      });
      
      setCurrentRoom(roomId);
      
      // Clear notifications for this room
      setNotifications(prev => prev.filter(notif => notif.roomId !== roomId));
      
      return true;
    }
    return false;
  };
  
  const leaveRoom = () => {
    if (socket && isConnected && currentRoom) {
      socket.emit('leaveRoom', { roomId: currentRoom });
      setCurrentRoom(null);
      setRoomUsers([]);
      setTypingUsers([]);
      return true;
    }
    return false;
  };
  
  // Typing indicator
  const sendTypingStatus = (isTyping) => {
    if (socket && isConnected && currentUser && currentRoom) {
      if (isTyping) {
        socket.emit('typing', {
          roomId: currentRoom,
          userId: currentUser.id,
          username: currentUser.name || currentUser.email
        });
        
        // Clear existing timeout
        if (typingTimeoutRef.current) {
          clearTimeout(typingTimeoutRef.current);
        }
        
        // Set timeout to automatically stop typing status after 3 seconds
        typingTimeoutRef.current = setTimeout(() => {
          socket.emit('stopTyping', {
            roomId: currentRoom,
            userId: currentUser.id
          });
        }, 3000);
      } else {
        // Clear timeout if exists
        if (typingTimeoutRef.current) {
          clearTimeout(typingTimeoutRef.current);
        }
        
        socket.emit('stopTyping', {
          roomId: currentRoom,
          userId: currentUser.id
        });
      }
    }
  };
  
  // Mark message as read
  const markMessageAsRead = (messageId, roomId) => {
    if (socket && isConnected) {
      socket.emit('markAsRead', { messageId, roomId });
    }
  };
  
  // Dismiss notification
  const dismissNotification = (notificationId) => {
    setNotifications(prev => prev.filter(notif => notif.id !== notificationId));
  };
  
  // Clear all notifications
  const clearAllNotifications = () => {
    setNotifications([]);
  };
  
  const value = {
    socket,
    isConnected,
    isReconnecting,
    currentRoom,
    roomUsers,
    typingUsers,
    notifications,
    messageStatus,
    sendMessage,
    joinRoom,
    leaveRoom,
    sendTypingStatus,
    markMessageAsRead,
    dismissNotification,
    clearAllNotifications
  };
  
  return (
    <SocketContext.Provider value={value}>
      {children}
    </SocketContext.Provider>
  );
}

export default SocketContext;
