import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import './Cart.css';

const Cart = () => {
  const { cartItems, removeItem, updateItemQuantity, clearCart, calculateTotal } = useCart();
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const navigate = useNavigate();

  const handleQuantityChange = (productId, newQuantity) => {
    if (newQuantity < 1) return;
    updateItemQuantity(productId, newQuantity);
  };

  const handleRemoveItem = (productId) => {
    if (window.confirm('Are you sure you want to remove this item?')) {
      removeItem(productId);
    }
  };

  const handleClearCart = () => {
    if (window.confirm('Are you sure you want to clear your cart?')) {
      clearCart();
    }
  };

  const handleCheckout = () => {
    setIsCheckingOut(true);
    // Simulate checkout process
    setTimeout(() => {
      alert('Checkout successful! Order has been placed.');
      clearCart();
      navigate('/');
    }, 1500);
  };

  if (cartItems.length === 0) {
    return (
      <div className="cart-container empty-cart">
        <h1>Your Cart</h1>
        <div className="empty-cart-message">
          <i className="cart-icon">ðŸ›’</i>
          <p>Your cart is empty</p>
          <Link to="/" className="continue-shopping-btn">Continue Shopping</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      
      <div className="cart-header">
        <span className="cart-header-product">Product</span>
        <span className="cart-header-price">Price</span>
        <span className="cart-header-quantity">Quantity</span>
        <span className="cart-header-total">Total</span>
        <span className="cart-header-actions">Actions</span>
      </div>
      
      <div className="cart-items">
        {cartItems.map(item => (
          <div key={item.id} className="cart-item">
            <div className="cart-item-product">
              <img 
                src={item.images?.[0] || 'https://via.placeholder.com/80'} 
                alt={item.title} 
              />
              <div className="cart-item-details">
                <h3>{item.title}</h3>
                {item.category && <span className="cart-item-category">{item.category.name}</span>}
              </div>
            </div>
            
            <div className="cart-item-price">${item.price}</div>
            
            <div className="cart-item-quantity">
              <button 
                className="quantity-btn"
                onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                disabled={item.quantity <= 1}
              >
                -
              </button>
              <span>{item.quantity}</span>
              <button 
                className="quantity-btn"
                onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
              >
                +
              </button>
            </div>
            
            <div className="cart-item-total">
              ${(item.price * item.quantity).toFixed(2)}
            </div>
            
            <div className="cart-item-actions">
              <button 
                className="remove-btn"
                onClick={() => handleRemoveItem(item.id)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>
      
      <div className="cart-summary">
        <div className="cart-total">
          <span>Total:</span>
          <span>${calculateTotal().toFixed(2)}</span>
        </div>
        
        <div className="cart-actions">
          <button 
            className="clear-cart-btn"
            onClick={handleClearCart}
          >
            Clear Cart
          </button>
          <Link to="/" className="continue-shopping-btn">Continue Shopping</Link>
          <button 
            className="checkout-btn"
            onClick={handleCheckout}
            disabled={isCheckingOut}
          >
            {isCheckingOut ? 'Processing...' : 'Proceed to Checkout'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
