import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product, onAddToCart }) => {
  const { id, title, price, images, category } = product;
  const [isHovered, setIsHovered] = useState(false);
  
  // Simulate vinyl-specific info (for demo purposes)
  const getVinylInfo = () => {
    // Random genres
    const genres = ['Rock', 'Jazz', 'Classical', 'Hip Hop', 'Electronic', 'Soul', 'Blues', 'Pop'];
    // Random decades
    const decades = ['1960s', '1970s', '1980s', '1990s', '2000s', '2010s', '2020s'];
    // Random conditions
    const conditions = ['Mint', 'Near Mint', 'Very Good+', 'Very Good', 'Good', 'Fair'];
    
    return {
      genre: product.genre || genres[Math.floor(Math.random() * genres.length)],
      decade: product.decade || decades[Math.floor(Math.random() * decades.length)],
      condition: product.condition || conditions[Math.floor(Math.random() * conditions.length)]
    };
  };
  
  const vinylInfo = getVinylInfo();
  
  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onAddToCart(product);
  };
  
  const handleQuickView = (e) => {
    e.preventDefault();
    e.stopPropagation();
    // In a real app, this would open a quick view modal
    alert(`Quick View: ${title}`);
  };
  
  return (
    <div 
      className="product-card vinyl-sleeve"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <Link to={`/product/${id}`} className="product-link">
        <div className="record-hole"></div>
        <div className="record-grooves"></div>
        
        <div className="product-image">
          <img 
            src={images?.[0] || 'https://via.placeholder.com/300'} 
            alt={title} 
            loading="lazy"
          />
          {category && (
            <span className="product-category-tag">{category.name}</span>
          )}
          
          {isHovered && (
            <div className="quick-actions">
              <button 
                className="quick-action-btn"
                onClick={handleQuickView}
                aria-label="Quick view"
              >
                <span className="material-icons">visibility</span>
              </button>
              <button 
                className="quick-action-btn"
                onClick={handleAddToCart}
                aria-label="Add to cart"
              >
                <span className="material-icons">add_shopping_cart</span>
              </button>
            </div>
          )}
        </div>
        
        <div className="product-info">
          <h3 className="product-title">{title}</h3>
          
          <div className="vinyl-details">
            <div className="vinyl-badges">
              <span className="vinyl-badge genre">{vinylInfo.genre}</span>
              <span className="vinyl-badge decade">{vinylInfo.decade}</span>
              <span className="vinyl-badge condition">{vinylInfo.condition}</span>
            </div>
          </div>
          
          <div className="product-footer">
            <span className="product-price">${parseFloat(price).toFixed(2)}</span>
            <button 
              className="add-to-cart-btn"
              onClick={handleAddToCart}
              aria-label={`Add ${title} to cart`}
            >
              <span className="material-icons cart-icon">shopping_cart</span>
              Add
            </button>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
