import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import ProductCard from '../components/Product/ProductCard';
import GenreFilter from '../components/Filter/GenreFilter';
import DecadeFilter from '../components/Filter/DecadeFilter';
import { useCart } from '../contexts/CartContext';
import { useProducts } from '../contexts/ProductContext';
import './Search.css';

const Search = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchPerformed, setSearchPerformed] = useState(false);

  // Filter states
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [selectedDecades, setSelectedDecades] = useState([]);
  const [selectedConditions, setSelectedConditions] = useState([]);
  const [priceRange, setPriceRange] = useState([0, 300]);
  const [maxPrice, setMaxPrice] = useState(300);

  // Ref for infinite scroll
  const observerTarget = useRef(null);

  // Get products context
  const { 
    products, 
    filteredProducts, 
    loading, 
    error, 
    searchProducts, 
    loadMore, 
    pagination,
    updateFilters
  } = useProducts();

  const { addItem } = useCart();
  const location = useLocation();

  // Condition options
  const conditions = [
    'Mint',
    'Near Mint',
    'Very Good Plus',
    'Very Good',
    'Good',
    'Fair'
  ];

  // Check for query parameters on mount and when URL changes
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const queryFromUrl = queryParams.get('query');

    if (queryFromUrl) {
      setSearchQuery(queryFromUrl);
      searchProducts(queryFromUrl);
      setSearchPerformed(true);
    }
  }, [location.search, searchProducts]);

  // Set up intersection observer for infinite scrolling
  useEffect(() => {
    if (!observerTarget.current || loading || !pagination.hasMore) return;

    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          loadMore();
        }
      },
      { threshold: 0.1 }
    );

    // Store the current value of the ref in a variable for cleanup
    const currentTarget = observerTarget.current;
    observer.observe(currentTarget);

    return () => {
      if (currentTarget) {
        observer.unobserve(currentTarget);
      }
    };
  }, [loading, pagination.hasMore, loadMore]);

  // Calculate max price when products change
  useEffect(() => {
    if (products.length > 0) {
      const highestPrice = Math.max(...products.map(product => parseFloat(product.price) || 0));
      const newMaxPrice = Math.ceil(highestPrice);
      setMaxPrice(newMaxPrice);

      // Only reset the price range if it's still at default
      if (priceRange[1] === 300) {
        setPriceRange([0, newMaxPrice]);
      }
    }
  }, [products, priceRange]);

  // Apply filters to ProductContext when local filters change
  useEffect(() => {
    updateFilters({
      genre: selectedGenres,
      decade: selectedDecades,
      condition: selectedConditions,
      priceRange: priceRange
    });
  }, [selectedGenres, selectedDecades, selectedConditions, priceRange, updateFilters]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      searchProducts(searchQuery);
      setSearchPerformed(true);

      // Update URL with search query for sharing/bookmarking
      const url = new URL(window.location);
      url.searchParams.set('query', searchQuery);
      window.history.pushState({}, '', url);
    }
  };

  const handleGenreChange = (genres) => {
    setSelectedGenres(genres);
  };

  const handleDecadeChange = (decades) => {
    setSelectedDecades(decades);
  };

  const handleConditionChange = (condition) => {
    const updatedConditions = selectedConditions.includes(condition)
      ? selectedConditions.filter(c => c !== condition)
      : [...selectedConditions, condition];

    setSelectedConditions(updatedConditions);
  };

  const handlePriceRangeChange = (e, index) => {
    const value = Number(e.target.value);
    const newRange = [...priceRange];
    newRange[index] = value;

    // Ensure min <= max
    if (index === 0 && value > newRange[1]) {
      newRange[1] = value;
    } else if (index === 1 && value < newRange[0]) {
      newRange[0] = value;
    }

    setPriceRange(newRange);
  };

  const clearAllFilters = () => {
    setSelectedGenres([]);
    setSelectedDecades([]);
    setSelectedConditions([]);
    setPriceRange([0, maxPrice]);
  };

  const handleAddToCart = (product) => {
    addItem(product);

    // Show notification instead of alert
    const notification = document.createElement('div');
    notification.className = 'notification';
    notification.innerHTML = `
      <span class="material-icons">check_circle</span>
      <p>${product.title} added to cart!</p>
    `;
    document.body.appendChild(notification);

    // Fade in
    setTimeout(() => {
      notification.classList.add('show');
    }, 10);

    // Remove after 3 seconds
    setTimeout(() => {
      notification.classList.remove('show');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 3000);
  };

  return (
    <div className="search-page">
      <div className="search-header">
        <h1>Vinyl Record Search</h1>
        <p>Discover your next favorite album</p>
      </div>

      <div className="search-container">
        <form onSubmit={handleSubmit} className="search-form">
          <input
            type="text"
            value={searchQuery}
            onChange={handleSearchChange}
            placeholder="Search for records..."
            className="search-input"
          />
          <button 
            type="submit" 
            className="search-button"
            disabled={loading || !searchQuery.trim()}
          >
            <span className="material-icons">search</span>
            {loading && !pagination.hasMore ? 'Searching...' : 'Search'}
          </button>
        </form>

        {error && (
          <div className="search-error">
            <p>Error: {error}</p>
            <button onClick={() => searchProducts(searchQuery)}>Try Again</button>
          </div>
        )}

        {loading && !pagination.hasMore ? (
          <div className="search-loading">
            <div className="vinyl-loader">
              <div className="vinyl-record">
                <div className="vinyl-label"></div>
              </div>
            </div>
            <p>Searching for vinyl records...</p>
          </div>
        ) : searchPerformed ? (
          <div className="search-results-container">
            {products.length > 0 && (
              <div className="filters-sidebar">
                <div className="filters-header">
                  <h2>Filter Records</h2>
                  <button 
                    onClick={clearAllFilters} 
                    className="clear-all-btn"
                    disabled={selectedGenres.length === 0 && 
                             selectedDecades.length === 0 && 
                             selectedConditions.length === 0 &&
                             priceRange[0] === 0 && 
                             priceRange[1] === maxPrice}
                  >
                    Clear All Filters
                  </button>
                </div>

                <div className="filter-container">
                  <div className="filter-header">
                    <h3>Price Range</h3>
                  </div>
                  <div className="price-range-filter">
                    <div className="price-inputs">
                      <div className="price-input">
                        <label htmlFor="min-price">Min:</label>
                        <div className="price-wrapper">
                          <span className="currency-symbol">$</span>
                          <input
                            id="min-price"
                            type="number"
                            min="0"
                            max={maxPrice}
                            value={priceRange[0]}
                            onChange={(e) => handlePriceRangeChange(e, 0)}
                          />
                        </div>
                      </div>
                      <div className="price-input">
                        <label htmlFor="max-price">Max:</label>
                        <div className="price-wrapper">
                          <span className="currency-symbol">$</span>
                          <input
                            id="max-price"
                            type="number"
                            min="0"
                            max={maxPrice}
                            value={priceRange[1]}
                            onChange={(e) => handlePriceRangeChange(e, 1)}
                          />
                        </div>
                      </div>
                    </div>
                    <div className="price-range-slider">
                      <input
                        type="range"
                        min="0"
                        max={maxPrice}
                        value={priceRange[0]}
                        onChange={(e) => handlePriceRangeChange(e, 0)}
                        className="price-slider min-slider"
                      />
                      <input
                        type="range"
                        min="0"
                        max={maxPrice}
                        value={priceRange[1]}
                        onChange={(e) => handlePriceRangeChange(e, 1)}
                        className="price-slider max-slider"
                      />
                    </div>
                  </div>
                </div>

                <GenreFilter 
                  selectedGenres={selectedGenres} 
                  onGenreChange={handleGenreChange} 
                  onClearFilters={() => setSelectedGenres([])} 
                />

                <DecadeFilter 
                  selectedDecades={selectedDecades} 
                  onDecadeChange={handleDecadeChange} 
                  onClearFilters={() => setSelectedDecades([])} 
                />

                <div className="filter-container">
                  <div className="filter-header">
                    <h3>Condition</h3>
                    <button 
                      onClick={() => setSelectedConditions([])} 
                      className="clear-filter-btn"
                      aria-label="Clear condition filters"
                    >
                      Clear
                    </button>
                  </div>
                  <div className="filter-options">
                    {conditions.map(condition => (
                      <label key={condition} className="filter-option">
                        <input
                          type="checkbox"
                          checked={selectedConditions.includes(condition)}
                          onChange={() => handleConditionChange(condition)}
                        />
                        <span className="checkbox-custom"></span>
                        <span className="option-label">{condition}</span>
                      </label>
                    ))}
                  </div>
                </div>

                {(selectedGenres.length > 0 || selectedDecades.length > 0 || 
                  selectedConditions.length > 0 || priceRange[0] > 0 || 
                  priceRange[1] < maxPrice) && (
                  <div className="active-filters">
                    <h3>Active Filters:</h3>
                    <div className="filter-tags">
                      {selectedGenres.map(genre => (
                        <span key={genre} className="filter-tag">
                          {genre}
                          <button 
                            onClick={() => handleGenreChange(selectedGenres.filter(g => g !== genre))}
                            aria-label={`Remove ${genre} filter`}
                          >
                            笨表n                          </button>
                        </span>
                      ))}

                      {selectedDecades.map(decade => (
                        <span key={decade} className="filter-tag">
                          {decade}
                          <button 
                            onClick={() => handleDecadeChange(selectedDecades.filter(d => d !== decade))}
                            aria-label={`Remove ${decade} filter`}
                          >
                            笨表n                          </button>
                        </span>
                      ))}

                      {selectedConditions.map(condition => (
                        <span key={condition} className="filter-tag">
                          {condition}
                          <button 
                            onClick={() => handleConditionChange(condition)}
                            aria-label={`Remove ${condition} filter`}
                          >
                            笨表n                          </button>
                        </span>
                      ))}

                      {(priceRange[0] > 0 || priceRange[1] < maxPrice) && (
                        <span className="filter-tag">
                          ${priceRange[0]} - ${priceRange[1]}
                          <button 
                            onClick={() => setPriceRange([0, maxPrice])}
                            aria-label="Reset price range"
                          >
                            笨表n                          </button>
                        </span>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}

            <div className="search-results">
              <div className="search-results-info">
                <h2>
                  {products.length === 0 
                    ? 'No records found' 
                    : `Found ${filteredProducts.length} of ${products.length} record${products.length === 1 ? '' : 's'}`}
                </h2>
                {searchQuery && <p>Results for: "{searchQuery}"</p>}
              </div>

              {filteredProducts.length > 0 ? (
                <>
                  <div className="products-grid">
                    {filteredProducts.map(product => (
                      <ProductCard 
                        key={product.id}
                        product={product}
                        onAddToCart={handleAddToCart}
                      />
                    ))}
                  </div>

                  {/* Infinite scroll loader */}
                  {pagination.hasMore && (
                    <div 
                      ref={observerTarget} 
                      className="infinite-scroll-loader"
                    >
                      {loading && (
                        <div className="scroll-loading">
                          <div className="vinyl-mini-loader">
                            <div className="vinyl-record">
                              <div className="vinyl-label"></div>
                            </div>
                          </div>
                          <p>Loading more records...</p>
                        </div>
                      )}
                    </div>
                  )}
                </>
              ) : (
                <div className="no-results">
                  <p>No records match your filter criteria.</p>
                  <p>Try adjusting your filters or search for something else.</p>
                  <button className="clear-all-btn" onClick={clearAllFilters}>
                    Clear All Filters
                  </button>
                </div>
              )}
            </div>
          </div>
        ) : (
          <div className="search-prompt">
            <p>Enter a search term to find vinyl records</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Search;
