import React from 'react';
import './ProductCard.css';
import { Link } from 'react-router-dom';
import { useCart } from '../../contexts/CartContext';
import { useAuth } from '../../contexts/AuthContext';
import { useWishlist } from '../../contexts/WishlistContext';
import { toast } from 'react-toastify';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { currentUser } = useAuth();
  const { addToWishlist, removeFromWishlist, wishlist } = useWishlist();
  
  const {
    _id,
    name,
    price,
    description,
    imageUrl,
    category,
    countInStock,
    genre,
    decade,
    condition,
  } = product;
  
  const truncatedName = name.length > 40 ? `${name.substring(0, 40)}...` : name;
  
  const isInWishlist = wishlist.some(item => item._id === _id);
  
  const handleAddToCart = () => {
    if (!currentUser) {
      toast.error('Please log in to add items to your cart');
      return;
    }
    
    if (countInStock > 0) {
      addToCart(product);
      toast.success(`${name} added to cart!`);
    } else {
      toast.error('This item is out of stock');
    }
  };
  
  const handleWishlistToggle = () => {
    if (!currentUser) {
      toast.error('Please log in to manage your wishlist');
      return;
    }

    if (isInWishlist) {
      removeFromWishlist(_id);
      toast.info(`${name} removed from wishlist`);
    } else {
      addToWishlist(product);
      toast.success(`${name} added to wishlist!`);
    }
  };
  
  return (
    <div className="product-card">
      <div className="vinyl-sleeve">
        <div className="record-hole"></div>
        <div className="record-grooves"></div>
        
        <Link to={`/product/${_id}`} className="product-link">
          <div className="product-image">
            <img src={imageUrl} alt={name} />
            <div className="product-category-tag">{category}</div>
          </div>
          
          <div className="product-info">
            <h3 className="product-title">{truncatedName}</h3>
            
            <div className="vinyl-details">
              <div className="vinyl-badges">
                {genre && <span className="vinyl-badge genre">{genre}</span>}
                {decade && <span className="vinyl-badge decade">{decade}</span>}
                {condition && <span className="vinyl-badge condition">{condition}</span>}
              </div>
            </div>
            
            <div className="product-footer">
              <div className="product-price">${price.toFixed(2)}</div>
              <button 
                className="add-to-cart-btn" 
                onClick={(e) => {
                  e.preventDefault();
                  handleAddToCart();
                }}
                disabled={countInStock === 0}
              >
                <span className="material-icons cart-icon">shopping_cart</span>
                {countInStock > 0 ? 'Add' : 'Out of Stock'}
              </button>
            </div>
          </div>
        </Link>
        
        <div className="quick-actions">
          <button 
            className="quick-action-btn" 
            onClick={handleWishlistToggle}
            title={isInWishlist ? "Remove from Wishlist" : "Add to Wishlist"}
          >
            <span className="material-icons">
              {isInWishlist ? 'favorite' : 'favorite_border'}
            </span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
