import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import './Cart.css';

const Cart = () => {
  const { cartItems, removeItem, updateItemQuantity, clearCart, calculateTotal } = useCart();
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const [promoCode, setPromoCode] = useState('');
  const [promoApplied, setPromoApplied] = useState(false);
  const [discount, setDiscount] = useState(0);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');
  
  const navigate = useNavigate();

  // Calculate subtotal, shipping, and total
  const subtotal = calculateTotal();
  const shipping = subtotal > 0 ? 5.99 : 0;
  const discountAmount = promoApplied ? (subtotal * discount) : 0;
  const total = subtotal + shipping - discountAmount;

  // Handle quantity change
  const handleQuantityChange = (productId, newQuantity) => {
    if (newQuantity < 1) return;
    updateItemQuantity(productId, newQuantity);
  };

  // Handle remove item
  const handleRemoveItem = (productId) => {
    if (window.confirm('Are you sure you want to remove this item?')) {
      removeItem(productId);
      showMessage('Item removed from cart');
    }
  };

  // Handle clear cart
  const handleClearCart = () => {
    if (window.confirm('Are you sure you want to clear your cart?')) {
      clearCart();
      showMessage('Cart cleared');
    }
  };

  // Handle checkout
  const handleCheckout = () => {
    setIsCheckingOut(true);
    // Simulate checkout process
    setTimeout(() => {
      showMessage('Order placed successfully!', true);
      clearCart();
      setTimeout(() => {
        navigate('/');
      }, 1500);
    }, 1500);
  };
  
  // Handle promo code application
  const handleApplyPromo = (e) => {
    e.preventDefault();
    
    // Simple promo code validation
    if (promoCode.toLowerCase() === 'save10') {
      setDiscount(0.1); // 10% discount
      setPromoApplied(true);
      showMessage('Promo code applied: 10% discount');
    } else if (promoCode.toLowerCase() === 'save20') {
      setDiscount(0.2); // 20% discount
      setPromoApplied(true);
      showMessage('Promo code applied: 20% discount');
    } else {
      showMessage('Invalid promo code', false);
    }
  };
  
  // Show notification message
  const showMessage = (message, isSuccess = true) => {
    setNotificationMessage(message);
    setShowNotification(true);
    
    setTimeout(() => {
      setShowNotification(false);
    }, 3000);
  };
  
  // Clear promo code
  const handleClearPromo = () => {
    setPromoCode('');
    setPromoApplied(false);
    setDiscount(0);
    showMessage('Promo code removed');
  };

  if (cartItems.length === 0) {
    return (
      <div className="cart-container empty-cart">
        <h1>Your Shopping Cart</h1>
        <div className="empty-cart-message">
          <span className="material-icons cart-icon">shopping_cart</span>
          <p>Your cart is empty</p>
          <Link to="/" className="continue-shopping-btn">
            <span className="material-icons">arrow_back</span>
            Continue Shopping
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>Your Shopping Cart</h1>
      
      <div className="cart-content">
        <div className="cart-items-section">
          <div className="cart-header">
            <span className="cart-header-product">Product</span>
            <span className="cart-header-price">Price</span>
            <span className="cart-header-quantity">Quantity</span>
            <span className="cart-header-total">Total</span>
            <span className="cart-header-actions">Actions</span>
          </div>
          
          <div className="cart-items">
            {cartItems.map(item => (
              <div key={item.id} className="cart-item">
                <div className="cart-item-product">
                  <img 
                    src={item.images?.[0] || 'https://via.placeholder.com/80'} 
                    alt={item.title} 
                  />
                  <div className="cart-item-details">
                    <h3>{item.title}</h3>
                    {item.category && <span className="cart-item-category">{item.category.name}</span>}
                  </div>
                </div>
                
                <div className="cart-item-price">${parseFloat(item.price).toFixed(2)}</div>
                
                <div className="cart-item-quantity">
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                  >
                    <span className="material-icons">remove</span>
                  </button>
                  <span>{item.quantity}</span>
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                  >
                    <span className="material-icons">add</span>
                  </button>
                </div>
                
                <div className="cart-item-total">
                  ${(item.price * item.quantity).toFixed(2)}
                </div>
                
                <div className="cart-item-actions">
                  <button 
                    className="remove-btn"
                    onClick={() => handleRemoveItem(item.id)}
                  >
                    <span className="material-icons">delete</span>
                  </button>
                </div>
              </div>
            ))}
          </div>
          
          <div className="cart-actions">
            <button 
              className="clear-cart-btn"
              onClick={handleClearCart}
            >
              <span className="material-icons">delete_sweep</span>
              Clear Cart
            </button>
            <Link to="/" className="continue-shopping-btn">
              <span className="material-icons">arrow_back</span>
              Continue Shopping
            </Link>
          </div>
        </div>
        
        <div className="order-summary">
          <h2>Order Summary</h2>
          
          <div className="summary-row">
            <span>Subtotal</span>
            <span>${subtotal.toFixed(2)}</span>
          </div>
          
          <div className="summary-row">
            <span>Shipping</span>
            <span>${shipping.toFixed(2)}</span>
          </div>
          
          {promoApplied && (
            <div className="summary-row discount">
              <span>Discount</span>
              <span>-${discountAmount.toFixed(2)}</span>
            </div>
          )}
          
          <div className="summary-row total">
            <span>Total</span>
            <span>${total.toFixed(2)}</span>
          </div>
          
          <div className="promo-code">
            <h3>Promo Code</h3>
            <form onSubmit={handleApplyPromo}>
              <div className="promo-input">
                <input 
                  type="text" 
                  placeholder="Enter promo code"
                  value={promoCode}
                  onChange={(e) => setPromoCode(e.target.value)}
                  disabled={promoApplied}
                />
                {promoApplied ? (
                  <button 
                    type="button" 
                    className="promo-btn remove"
                    onClick={handleClearPromo}
                  >
                    <span className="material-icons">close</span>
                  </button>
                ) : (
                  <button 
                    type="submit" 
                    className="promo-btn apply"
                    disabled={!promoCode}
                  >
                    Apply
                  </button>
                )}
              </div>
            </form>
            <div className="promo-hint">Try: SAVE10, SAVE20</div>
          </div>
          
          <button 
            className="checkout-btn"
            onClick={handleCheckout}
            disabled={isCheckingOut}
          >
            <span className="material-icons">shopping_bag</span>
            {isCheckingOut ? 'Processing...' : 'Proceed to Checkout'}
          </button>
          
          <div className="secure-checkout">
            <span className="material-icons">lock</span>
            <span>Secure Checkout</span>
          </div>
          
          <div className="payment-methods">
            <span>We Accept:</span>
            <div className="payment-icons">
              <i className="payment-icon visa">Visa</i>
              <i className="payment-icon mastercard">MC</i>
              <i className="payment-icon amex">Amex</i>
              <i className="payment-icon paypal">PayPal</i>
            </div>
          </div>
        </div>
      </div>
      
      {showNotification && (
        <div className="cart-notification">
          <span className="material-icons">check_circle</span>
          {notificationMessage}
        </div>
      )}
    </div>
  );
};

export default Cart;
