import React, { useState, useEffect } from 'react';
import ProductCard from '../components/Product/ProductCard';
import { useCart } from '../contexts/CartContext';
import './Home.css';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  
  const { addItem } = useCart();
  
  // Fetch products and categories
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch('https://api.escuelajs.co/api/v1/products');
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        setProducts(data);
        
        // Extract unique categories
        const uniqueCategories = [...new Set(data.map(product => product.category?.name))].filter(Boolean);
        setCategories(uniqueCategories);
        
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    
    fetchProducts();
  }, []);
  
  // Handle add to cart
  const handleAddToCart = (product) => {
    addItem(product);
    // Show success notification instead of alert
    showNotification(`${product.title} added to cart!`);
  };
  
  // Simple notification system
  const showNotification = (message) => {
    const notification = document.createElement('div');
    notification.className = 'notification';
    notification.innerHTML = `
      <span class="material-icons">check_circle</span>
      <p>${message}</p>
    `;
    document.body.appendChild(notification);
    
    // Fade in
    setTimeout(() => {
      notification.classList.add('show');
    }, 10);
    
    // Remove after 3 seconds
    setTimeout(() => {
      notification.classList.remove('show');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 3000);
  };
  
  // Filter products by category and search query
  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'all' || product.category?.name === selectedCategory;
    const matchesSearch = product.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                          product.description?.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesCategory && matchesSearch;
  });
  
  // Get featured products (first 4 products)
  const featuredProducts = products.slice(0, 4);
  
  // Get trending products (random 8 products with price > 100)
  const trendingProducts = products
    .filter(product => parseFloat(product.price) > 100)
    .sort(() => 0.5 - Math.random())
    .slice(0, 8);

  return (
    <div className="home-container">
      <div className="hero-section">
        <div className="hero-content">
          <h1>Welcome to Vinyl Vault</h1>
          <p>Discover premium vinyl records, turntables, and accessories</p>
          <button className="hero-button" onClick={() => document.getElementById('products-section').scrollIntoView({behavior: 'smooth'})}>
            <span className="material-icons">album</span>
            Browse Collection
          </button>
        </div>
        <div className="hero-image">
          <img src="https://images.unsplash.com/photo-1461360228754-6e81c478b882?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2340&q=80" alt="Vinyl Records" />
        </div>
      </div>
      
      {!loading && !error && (
        <div className="featured-section">
          <h2>Featured Products</h2>
          <div className="featured-grid">
            {featuredProducts.map(product => (
              <ProductCard 
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
          </div>
        </div>
      )}
      
      <div id="products-section" className="main-products-section">
        <div className="section-header">
          <h2>All Products</h2>
          <div className="filter-controls">
            <div className="search-bar">
              <span className="material-icons search-icon">search</span>
              <input 
                type="text" 
                placeholder="Search products..." 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            
            <div className="category-filter">
              <span>Category:</span>
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="all">All Categories</option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>
          </div>
        </div>
        
        {loading ? (
          <div className="loading-container">
            <div className="loader"></div>
            <p>Loading products...</p>
          </div>
        ) : error ? (
          <div className="error-container">
            <span className="material-icons error-icon">error</span>
            <p>Error: {error}</p>
            <button onClick={() => window.location.reload()}>
              <span className="material-icons">refresh</span> Retry
            </button>
          </div>
        ) : (
          <>
            {filteredProducts.length > 0 ? (
              <div className="products-grid">
                {filteredProducts.map(product => (
                  <ProductCard 
                    key={product.id}
                    product={product}
                    onAddToCart={handleAddToCart}
                  />
                ))}
              </div>
            ) : (
              <div className="no-products">
                <span className="material-icons">search_off</span>
                <p>No products found. Try a different search or category.</p>
                <button onClick={() => {setSearchQuery(''); setSelectedCategory('all');}}>
                  <span className="material-icons">refresh</span> Reset Filters
                </button>
              </div>
            )}
          </>
        )}
      </div>
      
      {!loading && !error && trendingProducts.length > 0 && (
        <div className="trending-section">
          <h2>Trending Products</h2>
          <div className="trending-grid">
            {trendingProducts.map(product => (
              <ProductCard 
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
