import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import './Navbar.css';

// Record icon SVG component
const RecordIcon = () => (
  <svg className="record-icon" width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="11" stroke="currentColor" strokeWidth="2"/>
    <circle cx="12" cy="12" r="3" fill="currentColor"/>
    <circle cx="12" cy="12" r="7" stroke="currentColor" strokeWidth="1"/>
  </svg>
);

const Navbar = () => {
  const { currentUser, logout } = useAuth();
  const [cartCount, setCartCount] = useState(0);
  const navigate = useNavigate();
  
  // Update cart count from localStorage
  useEffect(() => {
    const updateCartCount = () => {
      const cart = JSON.parse(localStorage.getItem('cart')) || [];
      const totalItems = cart.reduce((sum, item) => sum + (item.quantity || 1), 0);
      setCartCount(totalItems);
    };
    
    // Initial count
    updateCartCount();
    
    // Set up event listener for cart updates
    window.addEventListener('storage', updateCartCount);
    
    // Custom event for cart updates from within the app
    window.addEventListener('cartUpdated', updateCartCount);
    
    return () => {
      window.removeEventListener('storage', updateCartCount);
      window.removeEventListener('cartUpdated', updateCartCount);
    };
  }, []);
  
  const handleLogout = async (e) => {
    e.preventDefault();
    if (window.confirm('Are you sure you want to logout?')) {
      try {
        await logout();
        // Clear cart and any other stored data
        localStorage.clear();
        // Navigate to login page
        navigate('/login');
      } catch (error) {
        console.error('Error during logout:', error);
      }
    }
  };
  
  const handleTitleClick = () => {
    navigate('/');
  };

  return (
    <div className="navbar">
      <div className="logo-container" onClick={handleTitleClick}>
        <RecordIcon />
        <h1>Vinyl Vault</h1>
      </div>
      <nav>
        <ul className="main-nav">
          <li>
            <NavLink 
              to="/records" 
              className={({ isActive }) => isActive ? 'active' : ''}
            >
              Records
            </NavLink>
          </li>
          <li>
            <NavLink 
              to="/turntables" 
              className={({ isActive }) => isActive ? 'active' : ''}
            >
              Turntables
            </NavLink>
          </li>
          <li>
            <NavLink 
              to="/accessories" 
              className={({ isActive }) => isActive ? 'active' : ''}
            >
              Accessories
            </NavLink>
          </li>
          <li>
            <NavLink 
              to="/trade-in" 
              className={({ isActive }) => isActive ? 'active' : ''}
            >
              Trade-In
            </NavLink>
          </li>
        </ul>
        <ul className="user-nav">
          <li>
            <NavLink 
              to="/search" 
              className={({ isActive }) => isActive ? 'active' : ''}
              data-tooltip="Search Products"
            >
              <span className="material-icons">search</span>
            </NavLink>
          </li>
          <li>
            <NavLink 
              to="/cart" 
              className={({ isActive }) => isActive ? 'active' : ''}
              data-tooltip="Shopping Cart"
            >
              <span className="material-icons">shopping_cart</span>
              {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
            </NavLink>
          </li>
          <li>
            <NavLink 
              to="/profile" 
              className={({ isActive }) => isActive ? 'active' : ''}
              data-tooltip="Your Profile"
            >
              <span className="material-icons">person</span>
            </NavLink>
          </li>
          <li>
            <button 
              onClick={handleLogout}
              className="nav-button"
              data-tooltip="Logout"
            >
              <span className="material-icons">logout</span>
            </button>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Navbar;
